SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_ec_tit_rec ( nr_seq_pessoa_p bigint, nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_seq_lote_enc_contas_w	pessoa_encontro_contas.nr_seq_lote%type;
vl_recebido_w			titulo_receber_liq.vl_recebido%type;
vl_retorno_w			double precision;

BEGIN
 
select	max(a.nr_seq_lote) 
into STRICT	nr_seq_lote_enc_contas_w 
from	pessoa_encontro_contas a 
where	a.nr_sequencia	= nr_seq_pessoa_p;
 
select	coalesce(sum(a.vl_recebido),0) 
into STRICT	vl_recebido_w 
from	titulo_receber_liq a 
where	a.nr_seq_lote_enc_contas	= nr_seq_lote_enc_contas_w 
and	a.nr_titulo			= nr_titulo_p;
 
vl_retorno_w	:= vl_recebido_w;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
