CREATE OR REPLACE FUNCTION obter_valor_estoque_consig ( cd_estabelecimento_p bigint, dt_mesano_referencia_p timestamp, cd_local_estoque_p bigint, cd_material_p bigint, cd_fornecedor_p text) RETURNS bigint AS $body$
DECLARE


vl_estoque_w		double precision;


BEGIN

select	coalesce(sum(vl_estoque), 0)
into STRICT	vl_estoque_w
from	fornecedor_mat_consignado
where	cd_estabelecimento	= cd_estabelecimento_p
and	dt_mesano_referencia	= dt_mesano_referencia_p
and	cd_material		= cd_material_p
and	cd_fornecedor		= coalesce(cd_fornecedor_p, cd_fornecedor)
and	cd_local_estoque 		= coalesce(cd_local_estoque_p, cd_local_estoque);

return	vl_estoque_w;

end;
