CREATE OR REPLACE FUNCTION obter_valor_final_fluxo (cd_estabelecimento_p bigint, ie_classif_fluxo_p text, ie_periodo_p text, dt_inicial_p timestamp, dt_final_p timestamp, dt_atual_p timestamp, ie_tipo_saldo_p text, cd_conta_financ_p bigint) RETURNS bigint AS $body$
DECLARE


/*
ie_tipo_saldo_p
	3 - conta financeira acumulativa
*/
vl_fluxo_w			double precision := 0;
dt_referencia_w			timestamp;


BEGIN

if (ie_tipo_saldo_p = 3) then

	select	max(dt_referencia)
	into STRICT	dt_referencia_w
	from	fluxo_caixa
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_conta_financ		= cd_conta_financ_p
	and	ie_classif_fluxo	= ie_classif_fluxo_p
	and	ie_periodo		= ie_periodo_p
	and	dt_referencia		<= dt_final_p;

	if (dt_referencia_w IS NOT NULL AND dt_referencia_w::text <> '') then
		select	coalesce(max(vl_fluxo),0)
		into STRICT	vl_fluxo_w
		from	fluxo_caixa
		where	cd_estabelecimento	= cd_estabelecimento_p
		and	cd_conta_financ		= cd_conta_financ_p
		and	ie_classif_fluxo	= ie_classif_fluxo_p
		and	ie_periodo		= ie_periodo_p
		and	dt_referencia		= dt_referencia_w;
	end if;

end if;

return	vl_fluxo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
