CREATE OR REPLACE FUNCTION obter_valor_fluxo_saldo_lote ( cd_estabelecimento_p bigint, ie_classif_fluxo_p text, dt_inicial_p timestamp, dt_final_p timestamp, ie_tipo_saldo_p text) RETURNS bigint AS $body$
DECLARE


/*--------------------------------------------------------------- ATENÇÃO ----------------------------------------------------------------*/

/* Cuidado ao realizar alterações no fluxo de caixa em lote. Toda e qualquer alteração realizada em qualquer uma */

/* das procedures do fluxo de caixa em lote deve ser cuidadosamente verificada e realizada no fluxo de caixa      */

/* convencional. Devemos garantir que os dois fluxos de caixa tragam os mesmos valores no resultado, evitando     */

/* assim que existam diferenças entre os fluxos de caixa.                                                                                                */

/*--------------- AO ALTERAR O FLUXO DE CAIXA EM LOTE ALTERAR TAMBÉM O FLUXO DE CAIXA ---------------*/

/*
ie_tipo_saldo_p
	1 - Saldo anterior
	2 - Saldo atual
*/
vl_fluxo_w			fluxo_caixa_data.vl_fluxo%type := 0;
cd_conta_financ_saldo_w		parametro_fluxo_caixa.cd_conta_financ_saldo%type;
cd_conta_financ_sant_w		parametro_fluxo_caixa.cd_conta_financ_sant%type;
dt_referencia_w			timestamp;
ie_classif_fluxo_w		fluxo_caixa_lote.ie_classif_fluxo%type;


BEGIN

select	cd_conta_financ_saldo,
	cd_conta_financ_sant
into STRICT	cd_conta_financ_saldo_w,
	cd_conta_financ_sant_w
from	parametro_fluxo_caixa
where	cd_estabelecimento	= cd_estabelecimento_p;

ie_classif_fluxo_w			:= ie_classif_fluxo_p;

if (ie_tipo_saldo_p = 1) then

	select	min(b.dt_referencia)
	into STRICT	dt_referencia_w
	from	fluxo_caixa_lote a,
		fluxo_caixa_data b
	where	a.nr_sequencia		= b.nr_seq_lote_fluxo
	and	b.cd_estabelecimento	= cd_estabelecimento_p
	and	b.cd_conta_financ	= cd_conta_financ_sant_w
	and	a.ie_classif_fluxo	= ie_classif_fluxo_w
	and	b.dt_referencia		>= dt_inicial_p;

	if (dt_referencia_w IS NOT NULL AND dt_referencia_w::text <> '') then

		select	coalesce(max(b.vl_fluxo),0)
		into STRICT	vl_fluxo_w
		from	fluxo_caixa_lote a,
			fluxo_caixa_data b
		where	a.nr_sequencia		= b.nr_seq_lote_fluxo
		and	b.cd_estabelecimento	= cd_estabelecimento_p
		and	b.cd_conta_financ	= cd_conta_financ_sant_w
		and	a.ie_classif_fluxo	= ie_classif_fluxo_w
		and	b.dt_referencia		= dt_referencia_w;
	end if;

elsif (ie_tipo_saldo_p = 2) then

	select	max(b.dt_referencia)
	into STRICT	dt_referencia_w
	from	fluxo_caixa_lote a,
		fluxo_caixa_data b
	where	a.nr_sequencia		= b.nr_seq_lote_fluxo
	and	b.cd_estabelecimento	= cd_estabelecimento_p
	and	b.cd_conta_financ	= cd_conta_financ_saldo_w
	and	a.ie_classif_fluxo	= ie_classif_fluxo_w
	and	b.dt_referencia		<= dt_final_p;

	if (dt_referencia_w IS NOT NULL AND dt_referencia_w::text <> '') then

		select	coalesce(max(b.vl_fluxo),0)
		into STRICT	vl_fluxo_w
		from	fluxo_caixa_lote a,
			fluxo_caixa_data b
		where	a.nr_sequencia		= b.nr_seq_lote_fluxo
		and	b.cd_estabelecimento	= cd_estabelecimento_p
		and	b.cd_conta_financ	= cd_conta_financ_saldo_w
		and	a.ie_classif_fluxo	= ie_classif_fluxo_w
		and	b.dt_referencia		= dt_referencia_w;
	end if;

end if;

return vl_fluxo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_fluxo_saldo_lote ( cd_estabelecimento_p bigint, ie_classif_fluxo_p text, dt_inicial_p timestamp, dt_final_p timestamp, ie_tipo_saldo_p text) FROM PUBLIC;

