CREATE OR REPLACE FUNCTION obter_valor_fluxo_saldo (cd_estabelecimento_p bigint, ie_classif_fluxo_p text, ie_periodo_p text, dt_inicial_p timestamp, dt_final_p timestamp, dt_atual_p timestamp, ie_tipo_saldo_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_tipo_saldo_p
	1 - Saldo anterior
	2 - Saldo atual
*/
vl_fluxo_w			double precision := 0;
cd_conta_financ_saldo_w		bigint;
cd_conta_financ_sant_w		bigint;
dt_referencia_w			timestamp;
ie_classif_fluxo_w			varchar(100);


BEGIN

select	cd_conta_financ_saldo,
	cd_conta_financ_sant
into STRICT	cd_conta_financ_saldo_w,
	cd_conta_financ_sant_w
from	parametro_fluxo_caixa
where	cd_estabelecimento	= cd_estabelecimento_p;

ie_classif_fluxo_w			:= ie_classif_fluxo_p;
if (coalesce(ie_classif_fluxo_p::text, '') = '') then
	if (ie_tipo_saldo_p = 1) then
		if (dt_inicial_p < dt_atual_p) then
			ie_classif_fluxo_w	:= 'P';
		else
			ie_classif_fluxo_w	:= 'R';
		end if;
	elsif (ie_tipo_saldo_p = 2) then
		if (dt_final_p < dt_atual_p) then
			ie_classif_fluxo_w	:= 'P';
		else
			ie_classif_fluxo_w	:= 'R';
		end if;
	end if;
end if;

if (ie_tipo_saldo_p = 1) then

	select	min(dt_referencia)
	into STRICT	dt_referencia_w
	from	fluxo_caixa
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_conta_financ		= cd_conta_financ_sant_w
	and	ie_classif_fluxo		= ie_classif_fluxo_w
	and	ie_periodo		= ie_periodo_p
	and	dt_referencia		>= dt_inicial_p;

	if (dt_referencia_w IS NOT NULL AND dt_referencia_w::text <> '') then
		select	coalesce(max(vl_fluxo),0)
		into STRICT	vl_fluxo_w
		from	fluxo_caixa
		where	cd_estabelecimento	= cd_estabelecimento_p
		and	cd_conta_financ		= cd_conta_financ_sant_w
		and	ie_classif_fluxo		= ie_classif_fluxo_w
		and	ie_periodo		= ie_periodo_p
		and	dt_referencia		= dt_referencia_w;
	end if;

elsif (ie_tipo_saldo_p = 2) then

	select	max(dt_referencia)
	into STRICT	dt_referencia_w
	from	fluxo_caixa
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_conta_financ		= cd_conta_financ_saldo_w
	and	ie_classif_fluxo		= ie_classif_fluxo_w
	and	ie_periodo		= ie_periodo_p
	and	dt_referencia		<= dt_final_p;

	if (dt_referencia_w IS NOT NULL AND dt_referencia_w::text <> '') then
		select	coalesce(max(vl_fluxo),0)
		into STRICT	vl_fluxo_w
		from	fluxo_caixa
		where	cd_estabelecimento		= cd_estabelecimento_p
		and	cd_conta_financ		= cd_conta_financ_saldo_w
		and	ie_classif_fluxo		= ie_classif_fluxo_w
		and	ie_periodo		= ie_periodo_p
		and	dt_referencia		= dt_referencia_w;
	end if;

end if;

return vl_fluxo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
