CREATE OR REPLACE FUNCTION obter_valor_glosa_procmat ( nr_interno_conta_p bigint, cd_procedimento_p bigint, cd_material_p bigint, NR_SEQ_RETORNO_P bigint, nr_seq_procmat_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*	A = Valor adicional
	G = Valor glosa */
vl_glosa_w	double precision := 0;
vl_retorno_w	double precision := 0;
vl_adicional_w	double precision := 0;


BEGIN

if (ie_opcao_p = 'G') then
	if (coalesce(cd_material_p,0) <> 0) then
		select	coalesce(sum(a.vl_glosa),0)
		into STRICT	vl_glosa_w
		from	convenio_retorno_glosa a,
			convenio_retorno_item b
		where	a.nr_seq_ret_item	= b.nr_sequencia
		and	a.nr_seq_matpaci	= nr_seq_procmat_p
		and	b.nr_interno_conta	= nr_interno_conta_p
		and	a.cd_material		= cd_material_p
		AND	B.NR_SEQ_RETORNO	= NR_SEQ_RETORNO_P
		having 	min(b.nr_seq_retorno)	= NR_SEQ_RETORNO_P;
	elsif (coalesce(cd_procedimento_p,0) <> 0) then
		select	coalesce(sum(a.vl_glosa),0)
		into STRICT	vl_glosa_w
		from	convenio_retorno_glosa a,
			convenio_retorno_item b
		where	a.nr_seq_ret_item	= b.nr_sequencia
		and	a.nr_seq_propaci	= nr_seq_procmat_p
		and	b.nr_interno_conta	= nr_interno_conta_p
		and	a.cd_procedimento	= cd_procedimento_p
		AND	B.NR_SEQ_RETORNO	= NR_SEQ_RETORNO_P
		having 	min(b.nr_seq_retorno)	= NR_SEQ_RETORNO_P;
	end if;
	vl_retorno_w := vl_glosa_w;
elsif (ie_opcao_p = 'A') then
	if (coalesce(cd_material_p,0) <> 0) then
		select	coalesce(sum(a.vl_amaior),0)
		into STRICT	vl_adicional_w
		from	convenio_retorno_glosa a,
			convenio_retorno_item b
		where	a.nr_seq_ret_item	= b.nr_sequencia
		and	a.nr_seq_matpaci	= nr_seq_procmat_p
		and	b.nr_interno_conta	= nr_interno_conta_p
		and	a.cd_material		= cd_material_p
		AND	B.NR_SEQ_RETORNO	= NR_SEQ_RETORNO_P
		having 	min(b.nr_seq_retorno)	= NR_SEQ_RETORNO_P;
	elsif (coalesce(cd_procedimento_p,0) <> 0) then
		select	coalesce(sum(a.vl_amaior),0)
		into STRICT	vl_adicional_w
		from	convenio_retorno_glosa a,
			convenio_retorno_item b
		where	a.nr_seq_ret_item	= b.nr_sequencia
		and	a.nr_seq_propaci	= nr_seq_procmat_p
		and	b.nr_interno_conta	= nr_interno_conta_p
		and	a.cd_procedimento	= cd_procedimento_p
		AND	B.NR_SEQ_RETORNO	= NR_SEQ_RETORNO_P
		having 	min(b.nr_seq_retorno)	= NR_SEQ_RETORNO_P;
	end if;
	vl_retorno_w := vl_adicional_w;
end if;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
