CREATE OR REPLACE FUNCTION obter_valor_glosa_prot_doc (nr_seq_protocolo_p bigint) RETURNS bigint AS $body$
DECLARE

nr_seq_retorno_w	bigint;
vl_glosa_w		double precision := 0;

BEGIN
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_retorno_w
	from	convenio_retorno
	where	nr_seq_protocolo = nr_seq_protocolo_p;
end if;

select	sum(vl_glosado)
into STRICT	vl_glosa_w
from	convenio_retorno_item
where	nr_seq_retorno = nr_seq_retorno_w;

return	vl_glosa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
