SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_glosa_tit ( nr_titulo_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

 
/*	ie_opcao_p 
'N'	- Glosa normal 
'P'	- Glosa posterior 
*/
 
 
vl_glosa_w	double precision;


BEGIN 
 
if (ie_opcao_p = 'N') then 
 
	select	coalesce(sum(d.vl_glosa),0) 
	into STRICT	vl_glosa_w 
	from	lote_audit_hist_item d, 
		lote_auditoria c, 
		lote_audit_hist b, 
		lote_audit_hist_guia a 
	where	(obter_titulo_lote_guia(a.nr_interno_conta, a.cd_autorizacao, a.nr_seq_lote_hist))::numeric 	= nr_titulo_p 
	and	a.nr_seq_lote_hist	= b.nr_sequencia 
	and	(b.dt_envio IS NOT NULL AND b.dt_envio::text <> '') 
	and	b.nr_seq_lote_audit	= c.nr_sequencia 
	and	a.nr_sequencia		= d.nr_seq_guia 
	and	a.vl_saldo_guia		> 0;
 
elsif (ie_opcao_p = 'P') then 
	select	coalesce(sum(d.vl_glosa),0) 
	into STRICT	vl_glosa_w 
	from	lote_audit_hist_item d, 
		lote_auditoria c, 
		lote_audit_hist b, 
		lote_audit_hist_guia a 
	where	(obter_titulo_lote_guia(a.nr_interno_conta, a.cd_autorizacao, a.nr_seq_lote_hist))::numeric 	= nr_titulo_p 
	and	a.nr_seq_lote_hist	= b.nr_sequencia 
	and	(b.dt_envio IS NOT NULL AND b.dt_envio::text <> '') 
	and	b.nr_seq_lote_audit	= c.nr_sequencia 
	and	a.nr_sequencia		= d.nr_seq_guia 
	and	a.vl_saldo_guia		= 0;
end if;
 
return	vl_glosa_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_glosa_tit ( nr_titulo_p bigint, ie_opcao_p text) FROM PUBLIC;

