CREATE OR REPLACE FUNCTION obter_valor_guia_grg (nr_interno_conta_p bigint, cd_autorizacao_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_opcao_p
'VG'	- Valor glosa
'VP'	- Valor pago
*/
vl_retorno_w	double precision	:= 0;
vl_glosa_w	double precision;
vl_pago_w	double precision;
ie_param_266_w	varchar(1) := 'N';

BEGIN
ie_param_266_w := obter_param_usuario(27, 266, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_param_266_w);
	select	coalesce(sum(b.vl_glosa),0),
		coalesce(sum(b.vl_pago),0)
	into STRICT	vl_glosa_w,
		vl_pago_w
	from	lote_audit_hist_item b,
		lote_audit_hist_guia a
	where	a.nr_interno_conta	= nr_interno_conta_p
	and	a.cd_autorizacao	= cd_autorizacao_p
	and	a.nr_sequencia		= b.nr_seq_guia
	and	(a.dt_baixa_glosa IS NOT NULL AND a.dt_baixa_glosa::text <> '');

if (ie_opcao_p	= 'VG') then
	vl_retorno_w	:= vl_glosa_w;
elsif (ie_opcao_p	= 'VP') and (coalesce(ie_param_266_w,'N') = 'N') then
	vl_retorno_w	:= vl_pago_w;
end if;

RETURN vl_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
