CREATE OR REPLACE FUNCTION obter_valor_histaudit_ret_item (nr_sequencia_p bigint, ie_acao_p bigint) RETURNS bigint AS $body$
DECLARE


/*
 0 - Não Auditado
 1 - Recebimento
 2 - Reapresentação
 3 - Glosa Devida
 4 - Glosa Indevida
 5 - Adequação a menor
 6 - Adequação a maior
*/
vl_retorno_w		double precision;


BEGIN

select coalesce(sum(vl_historico),0)
into STRICT vl_retorno_w
from	hist_audit_conta_paciente b,
	conta_paciente_ret_hist a
where a.nr_seq_hist_audit = b.nr_sequencia
  and a.nr_seq_ret_item = nr_sequencia_p
  and b.ie_acao  = ie_acao_p;

return vl_retorno_w;

end;
$body$
