CREATE OR REPLACE FUNCTION obter_valor_hora_medico_escala ( cd_medico_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w 		double precision;

C01 CURSOR FOR
	SELECT	vl_hora
	from	escala_valor_hora_medico
	where	cd_medico = cd_medico_p
	and	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp()) and coalesce(dt_final_vigencia, clock_timestamp())
	order by dt_inicio_vigencia; --Se existir duas deste médico no periodo será obtida a mais recente
BEGIN

open C01;
loop
fetch C01 into
	vl_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
