SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_hora_rat ( nr_seq_cliente_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w			bigint;
vl_hora_w				double precision;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	com_cliente_regra_valor
where	nr_seq_cliente	= nr_seq_cliente_p
and	dt_inicio_vigencia	= (
	SELECT	max(dt_inicio_vigencia)
	from	com_cliente_regra_valor
	where	nr_seq_cliente	= nr_seq_cliente_p
	and	dt_inicio_vigencia	<= dt_referencia_p);

select	max(vl_cobrar)
into STRICT	vl_hora_w
from	com_cli_regra_valor_item
where	nr_seq_regra			= nr_sequencia_w
and	ie_rat				= 'S';


return	coalesce(vl_hora_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
