CREATE OR REPLACE FUNCTION obter_valor_id (nr_seq_feature_p bigint) RETURNS varchar AS $body$
DECLARE


ds_valor_id_w	varchar(100) := '';
ds_valor_comeco_w	varchar(100) := '';
ds_valor_fim_w	varchar(100) := '';

C01 CURSOR FOR
	SELECT 	DS_IDENTIFICADOR
	from   	REG_AVALIACAO_CUSTOMER
	where 	NR_SEQ_FEATURE = nr_seq_feature_p
    order by
      regexp_replace(
        regexp_replace(
          regexp_replace(DS_IDENTIFICADOR,'(^|\.)(\d{3})','\1 \2'),
        '(^|\.)(\d{2})','\1 0\2'),
      '(^|\.)(\d)','\1 00\2');


BEGIN

open C01;
loop
fetch C01 into
	ds_valor_id_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select  max(SUBSTR(ds_valor_id_w, 1,LENGTH(ds_valor_id_w) -1)),
			max(SUBSTR(ds_valor_id_w,LENGTH(ds_valor_id_w) , 1)) + 1
	into STRICT 	ds_valor_comeco_w,
			ds_valor_fim_w
	;
	end;
end loop;
close C01;

	ds_valor_id_w := ds_valor_comeco_w || ds_valor_fim_w;

return	ds_valor_id_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
