CREATE OR REPLACE FUNCTION obter_valor_infundido_npa ( nr_prescricao_p bigint, ie_tipo_solucao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		prescr_solucao_evento.nr_sequencia%type;
qt_vol_infundido_w	prescr_solucao_evento.qt_vol_infundido%type;
nr_seq_nut_neo_w   	prescr_solucao_evento.nr_seq_nut_neo%type;

BEGIN

select	coalesce(max(nr_sequencia),0),
		max(nr_seq_nut_neo)
into STRICT	nr_sequencia_w,
		nr_seq_nut_neo_w
from	prescr_solucao_evento
where	nr_prescricao		= nr_prescricao_p
and		ie_tipo_solucao		= ie_tipo_solucao_p
and		ie_alteracao 		= 2
and  	nr_seq_motivo		= 1
and		ie_evento_valido	= 'S';

if (coalesce(nr_sequencia_w, 0) > 0) and (coalesce(nr_seq_nut_neo_w, 0) > 0) then

	select	coalesce(sum(qt_vol_infundido), 0)
	into STRICT	qt_vol_infundido_w
	from	prescr_solucao_evento
	where	nr_prescricao	= nr_prescricao_p
	and		ie_tipo_solucao	= ie_tipo_solucao_p
	and 	nr_sequencia > nr_sequencia_w
	and		nr_seq_nut_neo 	= nr_seq_nut_neo_w
	and		ie_alteracao 	= 2
	and		nr_seq_motivo <> 1
	and		ie_evento_valido	= 'S';

end if;

return	coalesce(qt_vol_infundido_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
