SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_item_avaliar ( nr_seq_item_p bigint, ds_valor_p text, qt_valor_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resultado_w			varchar(255) := null;
cd_dominio_w			bigint;


BEGIN

select	coalesce(max(cd_dominio),0)
into STRICT	cd_dominio_w
from	med_item_avaliar
where	nr_sequencia = nr_seq_item_p;

if (cd_dominio_w > 0) then
	begin
	if (qt_valor_p IS NOT NULL AND qt_valor_p::text <> '') then
		select	max(ds_valor_dominio)
		into STRICT	ds_resultado_w
		from	med_valor_dominio
		where	nr_seq_dominio	= cd_dominio_w
		and	vl_dominio	= to_char(qt_valor_p);
	end if;
	if (coalesce(ds_resultado_w::text, '') = '') and (ds_valor_p IS NOT NULL AND ds_valor_p::text <> '') then
		select	substr(ds_valor_dominio,1,50)
		into STRICT	ds_resultado_w
		from	med_valor_dominio
		where	nr_seq_dominio	= cd_dominio_w
		and	vl_dominio	= ds_valor_p;
	end if;
	end;
else
	begin
	if (ds_valor_p IS NOT NULL AND ds_valor_p::text <> '') then
		begin
		select	ds_resultado
		into STRICT	ds_resultado_w
		from	med_item_avaliar_res
		where	nr_seq_item	= nr_seq_item_p
		and	to_char(nr_seq_res)	= ds_valor_p;
		end;
	elsif (qt_valor_p IS NOT NULL AND qt_valor_p::text <> '') then
		begin
		select	ds_resultado
		into STRICT	ds_resultado_w
		from	med_item_avaliar_res
		where	nr_seq_item	= nr_seq_item_p
		and	nr_seq_res	= qt_valor_p;
		end;
	end if;
	end;
end if;

return ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
