CREATE OR REPLACE FUNCTION obter_valor_item_nf_credito (nr_sequencia_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


vl_result_w			double precision;
vl_itens_imposto_w		nf_credito_item.vl_itens_imposto%type;
vl_itens_sem_imposto_w		nf_credito_item.vl_itens_sem_imposto%type;
vl_imposto_w			nf_credito_item.vl_imposto%type;
vl_itens_sem_imposto_orig_w	nf_credito_item.vl_itens_sem_imposto_orig%type;
vl_itens_imposto_orig_w		nf_credito_item.vl_itens_imposto_orig%type;
vl_imposto_orig_w		nf_credito_item.vl_imposto_orig%type;

/*
S - Vl Item Sem Imposto
C - Vl Item Com Imposto
I - Vl Imposto
*/
BEGIN

select	max(vl_itens_imposto),
	max(vl_itens_sem_imposto),
	max(vl_imposto),
	max(vl_itens_sem_imposto_orig),
	max(vl_itens_imposto_orig),
	max(vl_imposto_orig)
into STRICT	vl_itens_imposto_w,
	vl_itens_sem_imposto_w,
	vl_imposto_w,
	vl_itens_sem_imposto_orig_w,
	vl_itens_imposto_orig_w,
	vl_imposto_orig_w
from	nf_credito_item
where	nr_sequencia = nr_sequencia_p;

vl_result_w := 0;

if (ie_tipo_p = 'S') then
	begin

	if (vl_itens_sem_imposto_w > vl_itens_sem_imposto_orig_w) then
		begin
		vl_result_w := vl_itens_sem_imposto_w - vl_itens_sem_imposto_orig_w;
		end;
	elsif (vl_itens_sem_imposto_w < vl_itens_sem_imposto_orig_w) then
		begin
		vl_result_w := vl_itens_sem_imposto_orig_w - vl_itens_sem_imposto_w;
		end;
	end if;

	end;
elsif (ie_tipo_p = 'C') then
	begin

	if (vl_itens_imposto_w > vl_itens_imposto_orig_w) then
		begin
		vl_result_w := vl_itens_imposto_w - vl_itens_imposto_orig_w;
		end;
	elsif (vl_itens_imposto_w < vl_itens_imposto_orig_w) then
		begin
		vl_result_w := vl_itens_imposto_orig_w - vl_itens_imposto_w;
		end;
	end if;

	end;
elsif (ie_tipo_p = 'I') then
	begin

	if (vl_imposto_w > vl_imposto_orig_w) then
		begin
		vl_result_w := vl_imposto_w - vl_imposto_orig_w;
		end;
	elsif (vl_imposto_w < vl_imposto_orig_w) then
		begin
		vl_result_w := vl_imposto_orig_w - vl_imposto_w;
		end;
	end if;

	end;
end if;

return	vl_result_w;

end;
