CREATE OR REPLACE FUNCTION obter_valor_item_nf (nr_sequencia_nf_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
VT - Soma dos valores totais dos itens da nota fiscal
VD - Soma dos valores dos descontos dos itens da nota fiscal
VL - Soma dos valores líquidos dos itens da nota fiscal
VU - Soma dos valores unitários dos itens da nota fiscal
*/
vl_total_item_nf_w	double precision;
vl_desconto_item_nf_w	double precision;
vl_liquido_item_nf_w	double precision;
vl_total_unit_item_nf_w	double precision;
vl_retorno_w		double precision;


BEGIN

select	coalesce(sum(vl_total_item_nf),0),
	coalesce(sum(vl_desconto),0),
	coalesce(sum(vl_liquido),0),
	coalesce(sum(vl_unitario_item_nf),0)
into STRICT	vl_total_item_nf_w,
	vl_desconto_item_nf_w,
	vl_liquido_item_nf_w,
	vl_total_unit_item_nf_w
from	nota_fiscal_item
where   nr_sequencia = nr_sequencia_nf_p;

if (ie_opcao_p	= 'VT') then
	vl_retorno_w	:= vl_total_item_nf_w;
elsif (ie_opcao_p	= 'VD') then
	vl_retorno_w	:= vl_desconto_item_nf_w;
elsif (ie_opcao_p	= 'VL') then
	vl_retorno_w	:= vl_liquido_item_nf_w;
elsif (ie_opcao_p	= 'VU') then
	vl_retorno_w	:= vl_total_unit_item_nf_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
