CREATE OR REPLACE FUNCTION obter_valor_item_regra (nr_sequencia_p nota_fiscal_item.nr_sequencia%type, nr_item_nf_p nota_fiscal_item.nr_item_nf%type) RETURNS bigint AS $body$
DECLARE


vl_retorno_w double precision;


BEGIN

begin
select  a.vl_liquido
into STRICT    vl_retorno_w
from    nota_fiscal_item a
where   a.nr_sequencia = nr_sequencia_p
and     a.nr_item_nf = nr_item_nf_p;
exception
  when no_data_found then
    vl_retorno_w := 0;
end;

return vl_retorno_w;
end;
