SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_juros_tit_integral (nr_titulo_p bigint, nr_seq_baixa_p bigint) RETURNS bigint AS $body$
DECLARE


dt_vencimento_w			timestamp;
dt_venc_original_w		timestamp;
dt_vencimento_atual_w		timestamp;
cd_estabelecimento_w		bigint;
cd_tipo_taxa_juro_w		titulo_receber.cd_tipo_taxa_juro%type;
ie_juros_multa_venc_orig_w	parametro_contas_receber.ie_juros_multa_venc_orig%type;
ie_data_juros_multa_w		varchar(50);
qt_dia_venc_w			bigint;
dt_recebimento_w		timestamp;
ie_tipo_taxa_w			tipo_taxa.ie_tipo_taxa%type;
pr_juros_w			double precision	:= 0;
tx_juros_w			double precision	:= 0;
vl_titulo_w			titulo_receber.vl_titulo%type;
vl_juros_total_w		double precision;
ie_aprop_jm_orig_w		varchar(1);
			

BEGIN

vl_juros_total_w := 0;

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	select	max(a.cd_estabelecimento),
		max(a.cd_tipo_taxa_juro),
		coalesce(max(a.ie_data_juros_multa),'DT_VENCIMENTO'),
		max(a.dt_pagamento_previsto),
		max(a.dt_vencimento),
		max(b.dt_recebimento),
		max(a.tx_juros),
		max(a.vl_titulo)
	into STRICT	cd_estabelecimento_w,
		cd_tipo_taxa_juro_w,
		ie_data_juros_multa_w,
		dt_vencimento_atual_w,
		dt_venc_original_w,
		dt_recebimento_w,
		tx_juros_w,
		vl_titulo_w
	from	titulo_receber a,
		titulo_receber_liq b
	where	a.nr_titulo = b.nr_titulo
	and	a.nr_titulo = nr_titulo_p
	and	b.nr_sequencia  = nr_seq_baixa_p;

	select	coalesce(max(ie_juros_multa_venc_orig),'N')
	into STRICT	ie_juros_multa_venc_orig_w
	from	parametro_contas_receber
	where	cd_estabelecimento	= cd_estabelecimento_w;

	select	coalesce(max(a.ie_aprop_jm_orig),'N')
	into STRICT	ie_aprop_jm_orig_w
	from	pls_parametros_cr	a
	where	a.cd_estabelecimento	= cd_estabelecimento_w;	
		
	if (ie_aprop_jm_orig_w = 'S') then
		dt_vencimento_w	:= dt_venc_original_w;
	else
		if	((ie_juros_multa_venc_orig_w = 'S') or (ie_juros_multa_venc_orig_w = 'U' AND ie_data_juros_multa_w = 'DT_VENCIMENTO')) then
			dt_vencimento_w	:= dt_venc_original_w;
		elsif	((ie_juros_multa_venc_orig_w = 'N') or (ie_juros_multa_venc_orig_w = 'U' AND ie_data_juros_multa_w = 'DT_PAGAMENTO_PREVISTO')) then
			dt_vencimento_w	:= dt_vencimento_atual_w;
		end if;
	end if;

	qt_dia_venc_w	:= trunc(dt_recebimento_w, 'dd') - trunc(dt_vencimento_w, 'dd');

	if (qt_dia_venc_w > 0) then
		select	max(ie_tipo_taxa)
		into STRICT	ie_tipo_taxa_w
		from	tipo_taxa
		where	cd_tipo_taxa	= cd_tipo_taxa_juro_w;

		if (ie_tipo_taxa_w = 'A') then
			pr_juros_w	:= dividir_sem_round((tx_juros_w)::numeric, 365) * qt_dia_venc_w;
		elsif (ie_tipo_taxa_w = 'M') then
			pr_juros_w	:= dividir_sem_round((tx_juros_w)::numeric, 30) * qt_dia_venc_w;
		elsif (ie_tipo_taxa_w = 'D') then
			pr_juros_w	:= tx_juros_w * qt_dia_venc_w;
		end if;

		if (ie_tipo_taxa_w = 'V') then
			vl_juros_total_w	:= tx_juros_w * qt_dia_venc_w;
		else
			vl_juros_total_w	:= vl_titulo_w * (dividir_sem_round((pr_juros_w)::numeric, 100));
		end if;
	end if;
end if;

return	vl_juros_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
