CREATE OR REPLACE FUNCTION obter_valor_likert_sae (nr_seq_diag_p bigint, nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

qt_total_w	bigint;
qt_atual_w	bigint;
qt_horas_reaval_noc_w	bigint;
ds_observacao_noc_w	varchar(255);


BEGIN

select 	count(*)  * 5
into STRICT	qt_total_w
from 	pe_diagnostico_Likert
where 	nr_seq_diag = nr_seq_diag_p;


select	sum(coalesce(a.ie_resultado,5))
into STRICT	qt_atual_w
from	PE_PRESCR_DIAG_LIKERT a,
	pe_diagnostico_Likert b
where	a.NR_SEQ_LIKERT	 = b.nr_sequencia
and	a.nr_seq_diag	= nr_sequencia_p;

select  	qt_horas_reaval_noc,
	ds_observacao_noc
into STRICT    	qt_horas_reaval_noc_w,
	ds_observacao_noc_w
from  	pe_prescr_diag
where 	nr_seq_diag = nr_seq_diag_p
and   	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p	= 'T') then
	return qt_total_w;
elsif (ie_opcao_p	= 'A') then
	return qt_atual_w;
elsif (ie_opcao_p	= 'P') then
	return dividir(qt_atual_w * 100,qt_total_w);
elsif (ie_opcao_p	= 'H') then
	return qt_horas_reaval_noc_w;
elsif (ie_opcao_p	= 'D') then	
	return ds_observacao_noc_w;	
end if;

return	null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
