CREATE OR REPLACE FUNCTION obter_valor_limite ( vl_valor_p bigint, vl_minimo_p bigint, vl_maximo_p bigint) RETURNS bigint AS $body$
DECLARE


vl_final_w	double precision;


BEGIN

if (vl_minimo_p IS NOT NULL AND vl_minimo_p::text <> '') and (vl_valor_p < vl_minimo_p) then
	vl_final_w	:= coalesce(vl_minimo_p,0);
elsif (vl_maximo_p IS NOT NULL AND vl_maximo_p::text <> '') and (vl_valor_p > vl_maximo_p) then
	vl_final_w	:= coalesce(vl_maximo_p,0);
else
	vl_final_w	:= coalesce(vl_valor_p,0);
end if;

RETURN	vl_final_w;

END;
