CREATE OR REPLACE FUNCTION obter_valor_mat_un_med (cd_material_p bigint, cd_unidade_medida_p text, cd_estabelecimento_p bigint, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE



vl_retorno_w  double precision;
cd_unidade_medida_compra_w	material.cd_unidade_medida_compra%type;
cd_unidade_medida_estoque_w	material.cd_unidade_medida_estoque%type;
cd_unidade_medida_consumo_w	material.cd_unidade_medida_consumo%type;
cd_unidade_medida_ult_compra_w	material.cd_unidade_medida_compra%type;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
begin

	select cd_unidade_medida_compra
	into STRICT   cd_unidade_medida_compra_w
	from   material
	where  cd_material = cd_material_p;

	select cd_unidade_medida_estoque
	into STRICT   cd_unidade_medida_estoque_w
	from   material
	where  cd_material = cd_material_p;

	select cd_unidade_medida_consumo
	into STRICT   cd_unidade_medida_consumo_w
	from   material
	where  cd_material = cd_material_p;

	cd_unidade_medida_ult_compra_w := obter_dados_ult_compra_data(cd_estabelecimento_p,cd_material_p,null,dt_final_p,null,'UC');

	vl_retorno_w := obter_dados_ult_compra_data(cd_estabelecimento_p,cd_material_p,null,dt_final_p,null,'VU');

	/* INFO: obter_dados_material
	   QEC - QT_Conv_Estoque_Consumo
	   QCE - QT_Conv_Compra_Estoque
	*/
	/* Se a unidade de medida solicitada é igual a unidade de compra do material */

	if (cd_unidade_medida_p = cd_unidade_medida_compra_w) then

		/* Se a unidade de medida da ultima compra é igual a unidade de estoque do material */

		if (cd_unidade_medida_ult_compra_w = cd_unidade_medida_estoque_w) then
			/* Então o valor solicitado é o valor da ultima compra multiplicado pela quantidade "Compra x Estoque", pois foi solicitado o valor da unidade de compra e a ultima compra foi feito com a unidade de estoque */

			vl_retorno_w := vl_retorno_w * obter_dados_material(cd_material_p,'QCE');
		/* Se a unidade de medida da ultima compra é igual a unidade de consumo do material */

		elsif (cd_unidade_medida_ult_compra_w = cd_unidade_medida_consumo_w) then
			/* Então o valor solicitado é o valor da ultima compra multiplicado pela quantidade "Estoque x Consumo"  e o resultado é multiplicado pela quantidade  "Compra x Estoque", pois foi solicitado o valor da unidade de compra e a ultima compra foi feito com a unidade de consumo*/

			vl_retorno_w := (vl_retorno_w * obter_dados_material(cd_material_p,'QEC')) * obter_dados_material(cd_material_p,'QCE');
		end if;

	/* Se a unidade de medida solicitada é igual a unidade de estoque do material */

	elsif (cd_unidade_medida_p = cd_unidade_medida_estoque_w) then

		/* Se a unidade de medida da ultima compra é igual a unidade de compra do material  */

		if (cd_unidade_medida_ult_compra_w = cd_unidade_medida_compra_w) then
			/* Então o valor solicitado é o valor da ultima compra dividido pela quantidade "Compra x Estoque", pois foi solicitado o valor da unidade de estoque e a ultima compra foi feito com a unidade de compra*/

			vl_retorno_w := vl_retorno_w / obter_dados_material(cd_material_p,'QCE');
		/* Se a unidade de medida da ultima compra é igual a unidade de consumo do material */

		elsif (cd_unidade_medida_ult_compra_w = cd_unidade_medida_consumo_w) then
			/* Então o valor solicitado é o valor da ultima compra multiplicado pela quantidade "Estoque x Consumo", pois foi solicitado o valor da unidade de estoque e a ultima compra foi feito com a unidade de consumo*/

			vl_retorno_w := vl_retorno_w * obter_dados_material(cd_material_p,'QEC');
		end if;

	/* Se a unidade de medida solicitada é igual a unidade de consumo do material */

	elsif (cd_unidade_medida_p = cd_unidade_medida_consumo_w) then

		/* Se a unidade de medida da ultima compra é igual a unidade de compra do material  */

		if (cd_unidade_medida_ult_compra_w = cd_unidade_medida_compra_w) then
			/* Então o valor solicitado é o valor da ultima compra dividido pela quantidade "Compra x Estoque"  e o resultado é dividido pela quantidade  "Estoque x Consumo", pois foi solicitado o valor da unidade de consumo e a ultima compra foi feito com a unidade de compra*/

			vl_retorno_w := (vl_retorno_w / obter_dados_material(cd_material_p,'QCE')) / obter_dados_material(cd_material_p,'QEC');
		/* Se a unidade de medida da ultima compra é igual a unidade de estoque do material */

		elsif (cd_unidade_medida_ult_compra_w = cd_unidade_medida_estoque_w) then
			/* Então o valor solicitado é o valor da ultima compra divido pela quantidade "Estoque x Consumo", pois foi solicitado o valor da unidade de consumo e a ultima compra foi feito com a unidade de estoque*/

			vl_retorno_w := vl_retorno_w / obter_dados_material(cd_material_p,'QEC');
		end if;

	end if;
end;
end if;

return	coalesce(vl_retorno_w,0);

end;
$body$
