CREATE OR REPLACE FUNCTION obter_valor_medico_proc ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


vl_Medico_w				double precision;
vl_Auxiliares_w			double precision;


BEGIN

select 	coalesce(CASE WHEN coalesce(ie_entra_conta,'N')='N' THEN  0  ELSE vl_medico END ,0)
into STRICT 	vl_medico_w
FROM procedimento_paciente a
LEFT OUTER JOIN regra_honorario b ON (a.ie_responsavel_credito = b.cd_regra)
WHERE a.nr_sequencia			= nr_sequencia_p;


select coalesce(sum(vl_conta),0)
into STRICT vl_auxiliares_w
from Procedimento_Participante
where nr_sequencia = nr_sequencia_p;


RETURN vl_medico_w + vl_auxiliares_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
