CREATE OR REPLACE FUNCTION obter_valor_moeda (cd_estabelecimento_p bigint, vl_origem_p bigint, cd_moeda_origem_p bigint, dt_parametro_p timestamp, ie_conta_receb_pagar_p text) RETURNS bigint AS $body$
DECLARE



vl_retorno_w		cotacao_moeda.vl_cotacao%type	:= 0;
vl_cotacao_origem_w	cotacao_moeda.vl_cotacao%type	:= 0;
cd_moeda_padrao_w	integer;
dt_cotacao_w		timestamp;
cd_moeda_w		integer;


BEGIN

if (coalesce(cd_moeda_origem_p,0) <> 0) then

	if (ie_conta_receb_pagar_p	= 'R') then
		begin

		select	cd_moeda_padrao
		into STRICT	cd_moeda_w
		from	parametro_contas_receber
		where	cd_estabelecimento	= cd_estabelecimento_p;

		end;
	elsif (ie_conta_receb_pagar_p	= 'P') then
		begin

		select	cd_moeda_padrao
		into STRICT	cd_moeda_w
		from	parametros_contas_pagar
		where	cd_estabelecimento	= cd_estabelecimento_p;

		end;
	end if;

	if (cd_moeda_origem_p	<> cd_moeda_w) then

		select	max(dt_cotacao)
		into STRICT	dt_cotacao_w
		from	cotacao_moeda
		where	dt_cotacao	<= dt_parametro_p
		and	cd_moeda	= cd_moeda_origem_p;

		select	coalesce(max(vl_cotacao),0)
		into STRICT	vl_cotacao_origem_w
		from	cotacao_moeda
		where	cd_moeda	= cd_moeda_origem_p
		and	dt_cotacao	= dt_cotacao_w;

		select	dividir(vl_origem_p, vl_cotacao_origem_w)
		into STRICT	vl_retorno_w
		;
	else
		vl_retorno_w	:= vl_origem_p;
	end if;

else
	vl_retorno_w	:= vl_origem_p;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
