CREATE OR REPLACE FUNCTION obter_valor_morse (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_descricao_w	varchar(2000);


BEGIN

select	max(QT_PONTUACAO)
into STRICT	ds_descricao_w
from	ESCALA_MORSE a
where	nr_sequencia	= (	SELECT	max(x.nr_sequencia)
				from	ESCALA_MORSE x
				where	x.nr_atendimento	= nr_atendimento_p
				and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
				and	coalesce(x.dt_inativacao::text, '') = '');

return SubStr(Obter_desc_escala_morse(ds_descricao_w), 1, 20);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
