CREATE OR REPLACE FUNCTION obter_valor_movto_pend_baixa ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
qt_registro_w	bigint;

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin

	select	count(*)
	into STRICT	qt_registro_w
	from	movto_banco_pend_baixa
	where	nr_seq_movto_pend = nr_sequencia_p;
	end;

	if (qt_registro_w = 0) then
		begin
		ds_retorno_w := 'S';
		end;
	end if;
end if;

return	ds_retorno_w;
end;
$body$
