CREATE OR REPLACE FUNCTION obter_valor_movto_transf_caixa (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_caixa_rec_w	bigint;
vl_troco_w		double precision;
vl_transacao_w		double precision;
vl_retorno_w		double precision;
ie_acao_w		varchar(5);

/*
OS 586049 em 17/07/2013
Function utilizada na GERAR_TRANSFERENCIA_CAIXA
criada para desconsiderar o valor do troco informado no recebimento na transação do monetário, nos casos
em que o tipo de recebimento é de baixa de adiantamento c/ título a receber
*/
BEGIN

select	max(ie_acao_rec_caixa)
into STRICT	ie_acao_w
from	caixa_receb a,
	tipo_recebimento b,
	movto_trans_financ c
where	a.cd_tipo_receb_caixa	= b.cd_tipo_recebimento
and	a.nr_sequencia		= c.nr_seq_caixa_rec
and	c.nr_sequencia		= nr_sequencia_p;

if (ie_acao_w = '34') then

	select	coalesce(vl_transacao,0),
		nr_seq_caixa_rec
	into STRICT	vl_transacao_w,
		nr_seq_caixa_rec_w
	from	movto_trans_financ
	where	nr_sequencia	= nr_sequencia_p;

	select	coalesce(vl_troco_informado,0)
	into STRICT	vl_troco_w
	from	caixa_receb a,
		tipo_recebimento b
	where	a.cd_tipo_receb_caixa	= b.cd_tipo_recebimento
	and	b.ie_acao_rec_caixa	= '34'
	and	a.nr_sequencia		= nr_seq_caixa_rec_w;

	vl_retorno_w	:= vl_transacao_w - coalesce(vl_troco_w,0);

else

	select	coalesce(vl_transacao,0)
	into STRICT	vl_retorno_w
	from	movto_trans_financ
	where	nr_sequencia	= nr_sequencia_p;

end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_movto_transf_caixa (nr_sequencia_p bigint) FROM PUBLIC;

