SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_nc_titulo_cobr ( nr_seq_titulo_cobr_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	double precision;
qt_baixa_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_baixa_w
from	nota_credito_baixa a
where	a.nr_seq_tit_rec_cobr	= nr_seq_titulo_cobr_p;

if (qt_baixa_w	> 0) then
	select	sum(a.vl_baixa)
	into STRICT	vl_retorno_w
	from	nota_credito_baixa a
	where	a.nr_seq_tit_rec_cobr	= nr_seq_titulo_cobr_p;
end if;

if (coalesce(vl_retorno_w,0) = 0) then
	select	sum(a.vl_nota_credito)
	into STRICT	vl_retorno_w
	from	titulo_receber_nc a,
		titulo_receber_cobr b
	where	b.nr_sequencia	= nr_seq_titulo_cobr_p
	and	b.nr_titulo	= a.nr_titulo_rec
	and	coalesce(a.nr_seq_liq::text, '') = '';
end if;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
