SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_orc_pac_orig (nr_seq_orcamento_p bigint) RETURNS bigint AS $body$
DECLARE


vl_material_w			double precision;
vl_Procedimento_w		double precision;
vl_orcamento_w		double precision;
vl_desconto_w			double precision;
vl_desconto_proced_w		double precision;
vl_desconto_material_w		double precision;
vl_pacote_w			double precision;


BEGIN

vl_material_w			:= 0;
vl_Procedimento_w		:= 0;
vl_desconto_w			:= 0;
vl_pacote_w			:= 0;

if (nr_seq_orcamento_p IS NOT NULL AND nr_seq_orcamento_p::text <> '') then
	begin
	select	coalesce(sum(vl_procedimento),0)
	into STRICT	vl_procedimento_w
	from 	orcamento_paciente_proc
	where	nr_sequencia_orcamento = nr_seq_orcamento_p
	and 	coalesce(ie_agendavel,'N') = 'N';

	select	coalesce(sum(vl_material),0)
	into STRICT	vl_material_w
	from 	orcamento_paciente_mat
	where	nr_sequencia_orcamento = nr_seq_orcamento_p;

	select	coalesce(vl_desconto,0)
	into STRICT	vl_desconto_w
	from	orcamento_paciente
	where	nr_sequencia_orcamento = nr_seq_orcamento_p;


	-- Somar o valor do procedimento pacote que está definido como agendável em seu cadastro.
	select	coalesce(sum(a.vl_procedimento),0)
	into STRICT	vl_pacote_w
	from 	orcamento_paciente_proc a
	where	a.nr_sequencia_orcamento = nr_seq_orcamento_p
	and 	coalesce(a.ie_agendavel,'N') = 'S'
	and	coalesce(ie_pacote,'N') = 'S'
	and	exists (SELECT	1
			from	orcamento_pac_proc_valor x
			where	x.nr_seq_orc_proced = a.nr_sequencia);

	vl_procedimento_w := vl_procedimento_w + coalesce(vl_pacote_w,0);

	if (vl_desconto_w	= 0) then

		select	coalesce(sum(vl_desconto),0)
		into STRICT	vl_desconto_proced_w
		from	orcamento_paciente_proc
		where	nr_sequencia_orcamento = nr_seq_orcamento_p
		and 	coalesce(ie_valor_informado,'N') = 'N'
		and 	coalesce(ie_agendavel,'N') = 'N';

		select	coalesce(sum(vl_desconto),0)
		into STRICT	vl_desconto_material_w
		from	orcamento_paciente_mat
		where	nr_sequencia_orcamento = nr_seq_orcamento_p;

		vl_desconto_w		:= vl_desconto_proced_w + vl_desconto_material_w;

	end if;

	end;
end if;

vl_orcamento_w	:= round(vl_procedimento_w + vl_material_w - (vl_desconto_w),2);

Return vl_orcamento_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
