CREATE OR REPLACE FUNCTION obter_valor_original_pacote (nr_seq_proc_pacote_p bigint) RETURNS bigint AS $body$
DECLARE


vl_pacote_w          double precision;


BEGIN

if (nr_seq_proc_pacote_p IS NOT NULL AND nr_seq_proc_pacote_p::text <> '') then
	select  sum(a.vl_original)
	into STRICT    vl_pacote_w
	from    procedimento_paciente b,
	   	atendimento_pacote a
	where 	b.nr_sequencia = a.nr_seq_proc_origem
	  and 	a.nr_seq_procedimento = nr_seq_proc_pacote_p;


	if (coalesce(vl_pacote_w::text, '') = '') then
		select	sum(a.vl_procedimento)
		into STRICT	vl_pacote_w
		from	procedimento_paciente a
		where	nr_sequencia	= nr_seq_proc_pacote_p;
	end if;

end if;

RETURN coalesce(vl_pacote_w,0);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
