SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_orig_audit (nr_seq_auditoria_p bigint) RETURNS bigint AS $body$
DECLARE


vl_original_w	double precision;


BEGIN

select 	coalesce(sum(x.vl_item),0)
into STRICT	vl_original_w
from (SELECT coalesce(sum(vl_material),0) vl_item
	 from 	auditoria_matpaci
	 where 	nr_seq_auditoria =  nr_seq_auditoria_p
	 and    coalesce(ie_original,'N') = 'S'
	
union all

	 SELECT coalesce(sum(vl_procedimento),0) vl_item
	 from 	auditoria_propaci
	 where 	nr_seq_auditoria =  nr_seq_auditoria_p
	 and    coalesce(ie_original,'N') = 'S') x;

return	vl_original_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
