CREATE OR REPLACE FUNCTION obter_valor_orig_proc ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_origem_w		double precision;


BEGIN
select coalesce(max(vl_procedimento),0)
into STRICT	vl_origem_w
from 	proc_paciente_valor
where	nr_seq_procedimento	= nr_sequencia_p
  and	ie_tipo_valor		= 1;

if (vl_origem_w = 0) then
	select vl_procedimento
	into STRICT	vl_origem_w
	from procedimento_paciente
	where nr_sequencia	= nr_sequencia_p;
end if;
RETURN vl_origem_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
