CREATE OR REPLACE FUNCTION obter_valor_pacote_protocolo (nr_seq_protocolo_p bigint) RETURNS bigint AS $body$
DECLARE

			 
vl_retorno_w		double precision;
	

BEGIN 
 
select	coalesce(sum(p.vl_procedimento),0) 
into STRICT	vl_retorno_w 
from	procedimento_paciente p, 
	conta_paciente c, 
	protocolo_convenio r 
where	r.nr_seq_protocolo = nr_seq_protocolo_p 
and	r.nr_seq_protocolo = c.nr_seq_protocolo 
and	c.nr_interno_conta = p.nr_interno_conta 
and	nr_sequencia	  = nr_seq_proc_pacote;
 
return	vl_retorno_w;
 
end;
