SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_padrao_comp (ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
	pdm		- primeiro dia mês
	pdmp		- primeiro dia mês passado
	dta		- data atual
	dtaunt		- data atual no formado yyyymmdd.hh24miss
	dtafd		- data atual fim do dia
*/
ds_retorno_w	varchar(50);


BEGIN

if (ie_opcao_p = 'PDM') then
	ds_retorno_w	:= to_char(PKG_DATE_UTILS.start_of(clock_timestamp(),'MONTH', 0), 'dd/mm/yyyy');
elsif (ie_opcao_p = 'PDMP') then
	ds_retorno_w	:= to_char(PKG_DATE_UTILS.start_of(PKG_DATE_UTILS.ADD_MONTH(clock_timestamp(),-1, 0),'MONTH', 0), 'dd/mm/yyyy');
elsif (ie_opcao_p = 'DTA') then
	ds_retorno_w	:= to_char(clock_timestamp(), 'dd/mm/yyyy');
elsif (ie_opcao_p = 'DTAUNT') then
	ds_retorno_w	:= to_char(clock_timestamp(),'yyyymmdd.hh24miss');
elsif (ie_opcao_p = 'DTAFD') then
	ds_retorno_w	:= to_char(fim_dia(clock_timestamp()),'dd/mm/yyyy hh24:mi:ss');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
