CREATE OR REPLACE FUNCTION obter_valor_padrao_pergunta (nr_seq_modelo_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w       bigint;
vl_padrao_w  varchar(80);
ds_retorno_w varchar(2000);

C01 CURSOR FOR
     SELECT  b.nr_sequencia,
             b.vl_padrao
     from    modelo_conteudo b,
             modelo a
     where   a.nr_sequencia  = nr_seq_modelo_p
     and     a.nr_sequencia  = b.nr_seq_modelo
     and     (b.vl_padrao IS NOT NULL AND b.vl_padrao::text <> '');

BEGIN

open c01;
loop
fetch        c01 into
     nr_sequencia_w,
     vl_padrao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
     begin
     ds_retorno_w := ds_retorno_w || to_char(nr_sequencia_w) ||'='||to_char(vl_padrao_w) ||'#@#@';
     end;
end  loop;
close        c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
