SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_pago_convenio ( nr_interno_conta_p bigint, nr_seq_procedimento_p bigint, nr_seq_material_p bigint) RETURNS bigint AS $body$
DECLARE


vl_cobrado_w		double precision;
nr_doc_convenio_w	varchar(20);
vl_guia_w		double precision;
vl_pago_w		double precision;
vl_glosa_w		double precision;


BEGIN

if (nr_seq_procedimento_p IS NOT NULL AND nr_seq_procedimento_p::text <> '') then
	select	vl_procedimento,
		nr_doc_convenio
	into STRICT	vl_cobrado_w,
		nr_doc_convenio_w
	from	procedimento_paciente
	where	nr_sequencia	= nr_seq_procedimento_p;
else
	select	vl_material,
		nr_doc_convenio
	into STRICT	vl_cobrado_w,
		nr_doc_convenio_w
	from	material_atend_paciente
	where	nr_sequencia	= nr_seq_material_p;
end if;

if (nr_doc_convenio_w IS NOT NULL AND nr_doc_convenio_w::text <> '') then

	select	max(vl_guia),
		max(vl_convenio)
	into STRICT	vl_guia_w,
		vl_pago_w
	from conta_paciente_guia
	where nr_interno_conta	= nr_interno_conta_p
	and cd_autorizacao	= nr_doc_convenio_w;
else

	select	/*+index (A CONPAGU_PK */		max(a.vl_guia),
		max(a.vl_convenio)
	into STRICT	vl_guia_w,
		vl_pago_w
	from 	conta_paciente_guia a
	where 	a.nr_interno_conta		= nr_interno_conta_p
	and 	lower(a.cd_autorizacao)		= lower(wheb_mensagem_pck.get_texto(799555));
end if;



if (coalesce(vl_pago_w,0) < coalesce(vl_guia_w,0)) then
	if (nr_seq_procedimento_p IS NOT NULL AND nr_seq_procedimento_p::text <> '') then
		select	coalesce(sum(vl_glosa),0)
		into STRICT	vl_glosa_w
		from convenio_retorno_glosa
		where nr_seq_propaci = nr_seq_procedimento_p
		  and nr_seq_ret_item = (SELECT max(nr_seq_ret_item)
					from convenio_retorno_glosa
					where nr_seq_propaci = nr_seq_procedimento_p);
	else
		select	coalesce(sum(vl_glosa),0)
		into STRICT	vl_glosa_w
		from convenio_retorno_glosa
		where nr_seq_matpaci = nr_seq_material_p
		  and nr_seq_ret_item = (SELECT max(nr_seq_ret_item)
					from convenio_retorno_glosa
					where nr_seq_matpaci = nr_seq_material_p);
	end if;
end if;

return vl_cobrado_w - coalesce(vl_glosa_w,0);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
