CREATE OR REPLACE FUNCTION obter_valor_pago_prot ( nr_seq_protocolo_p bigint, ie_valor_p text, dt_saldo_p timestamp) RETURNS bigint AS $body$
DECLARE

 
 
vl_pago_w	double precision;
vl_amenor_w	double precision;
vl_glosado_w	double precision;
vl_adicional_w	double precision;

/* 
 
Opções: 
 
P - Pago 
AM - Amenor 
G - Glosado 
m - vl_amaior 
*/
 
 
 

BEGIN 
 
if (ie_valor_p = 'P') then 
    select coalesce(sum(vl_pago),0) 
    into STRICT  vl_pago_w 
    from  convenio_retorno b, 
        convenio_retorno_item a 
    where  a.nr_seq_retorno = b.nr_sequencia 
     and  nr_interno_conta in (SELECT x.nr_interno_conta from conta_paciente x where x.nr_seq_protocolo = nr_seq_protocolo_p) 
     and  b.ie_status_retorno = 'F' 
     and  b.dt_baixa_cr  <= coalesce(dt_saldo_p, clock_timestamp());
	 
	return vl_pago_w;
end if;
 
if (ie_valor_p = 'AM') then 
    select coalesce(sum(vl_amenor),0) 
    into STRICT  vl_amenor_w 
    from  convenio_retorno b, 
        convenio_retorno_item a 
    where  a.nr_seq_retorno = b.nr_sequencia 
     and  nr_interno_conta in (SELECT x.nr_interno_conta from conta_paciente x where x.nr_seq_protocolo = nr_seq_protocolo_p) 
     and  b.ie_status_retorno = 'F' 
     and  b.dt_baixa_cr  <= coalesce(dt_saldo_p, clock_timestamp());
 
	return vl_amenor_w;
end if;
 
if (ie_valor_p = 'G') then 
    select coalesce(sum(vl_glosado),0) 
    into STRICT  vl_glosado_w 
    from  convenio_retorno b, 
        convenio_retorno_item a 
    where  a.nr_seq_retorno = b.nr_sequencia 
     and  nr_interno_conta in (SELECT x.nr_interno_conta from conta_paciente x where x.nr_seq_protocolo = nr_seq_protocolo_p) 
     and  b.ie_status_retorno = 'F' 
     and  b.dt_baixa_cr  <= coalesce(dt_saldo_p, clock_timestamp());
 
	return vl_glosado_w;
end if;
 
if (ie_valor_p = 'M') then 
    select coalesce(sum(vl_adicional),0) 
    into STRICT  vl_adicional_w 
    from  convenio_retorno b, 
        convenio_retorno_item a 
    where  a.nr_seq_retorno = b.nr_sequencia 
     and  nr_interno_conta in (SELECT x.nr_interno_conta from conta_paciente x where x.nr_seq_protocolo = nr_seq_protocolo_p) 
     and  b.ie_status_retorno = 'F' 
     and  b.dt_baixa_cr  <= coalesce(dt_saldo_p, clock_timestamp());
 
	return vl_adicional_w;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
