CREATE OR REPLACE FUNCTION obter_valor_pago_tit_pagar ( nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


vl_baixa_w	double precision;


BEGIN

select 	coalesce(sum(vl_pago),0)
into STRICT		vl_baixa_w
from 		titulo_pagar_baixa
where		nr_titulo	= nr_titulo_p;

return	vl_baixa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
