CREATE OR REPLACE FUNCTION obter_valor_pago_tit_rec (nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_calculado_w		double precision;
vl_recebido_w		double precision;
vl_juros_w		double precision;
vl_multa_w		double precision;
vl_outros_acrescimos_w	double precision;
vl_rec_maior_w		double precision;
vl_descontos_w		double precision;
					

BEGIN 
 
select	coalesce(sum(vl_recebido),0), 
	coalesce(sum(vl_juros),0), 
	coalesce(sum(vl_multa),0), 
	coalesce(sum(vl_outros_acrescimos),0), 
	coalesce(sum(vl_rec_maior),0), 
	coalesce(sum(vl_descontos),0) 
into STRICT	vl_recebido_w, 
	vl_juros_w, 
	vl_multa_w, 
	vl_outros_acrescimos_w, 
	vl_rec_maior_w, 
	vl_descontos_w 
from	titulo_receber_liq 
where 	nr_titulo = nr_titulo_p;
 
vl_calculado_w := coalesce(vl_recebido_w,0) + coalesce(vl_juros_w,0) + coalesce(vl_multa_w,0) + coalesce(vl_outros_acrescimos_w,0) + coalesce(vl_rec_maior_w,0);
 
return	vl_calculado_w;
 
end;
$body$
