CREATE OR REPLACE FUNCTION obter_valor_param86_grg ( cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


vl_parametro_w	varchar(255);

BEGIN
        vl_parametro_w := Obter_Param_Usuario(69, 86, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, vl_parametro_w);

        if (vl_parametro_w = 'R') then
                BEGIN
                        select  coalesce(max(IE_REGRA_NOTA_DEB_CRED), 'N')
                        into STRICT    vl_parametro_w
                        from    CONVENIO_ESTABELECIMENTO
                        where   cd_convenio = cd_convenio_p
                        and     cd_estabelecimento = cd_estabelecimento_p;
                END;
        END if;

return vl_parametro_w;

END;
