CREATE OR REPLACE FUNCTION obter_valor_pendente_conta ( nr_interno_conta_p bigint, cd_autorizacao_p text) RETURNS bigint AS $body$
DECLARE


vl_pendente_w		double precision := 0;
vl_guia_w		double precision;
vl_pago_w		double precision;


BEGIN
select	sum(a.vl_guia)
into STRICT	vl_guia_w
from	conta_paciente_guia a
where 	a.nr_interno_conta	= nr_interno_conta_p
and	a.cd_autorizacao	= cd_autorizacao_p;

select	sum(a.vl_pago + a.vl_glosado)
into STRICT	vl_pago_w
from	convenio_retorno b,
	convenio_retorno_item a
where	a.nr_seq_retorno	= b.nr_sequencia
and	b.ie_status_retorno	= 'F'
and 	a.nr_interno_conta	= nr_interno_conta_p
and 	a.cd_autorizacao 	= cd_autorizacao_p;

vl_pendente_w	:= coalesce(vl_guia_w,0) - coalesce(vl_pago_w,0);
return vl_pendente_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
