CREATE OR REPLACE FUNCTION obter_valor_pend_conta_data (nr_seq_conta_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
nr_seq_conciliacao_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_conciliacao_w
from	conciliacao_bancaria a
where	nr_seq_conta	= nr_seq_conta_p
and	dt_conciliacao	=	(SELECT	max(x.dt_conciliacao)
				from	conciliacao_bancaria x
				where	x.nr_seq_conta	= a.nr_seq_conta
				and	trunc(x.dt_conciliacao,'dd') <= dt_referencia_p);

if (nr_seq_conciliacao_w IS NOT NULL AND nr_seq_conciliacao_w::text <> '') then
	select	coalesce(sum(CASE WHEN a.ie_banco='C' THEN  b.vl_transacao * -1 WHEN a.ie_banco='D' THEN  b.vl_transacao  ELSE 0 END ),0)
	into STRICT	vl_retorno_w
	from	transacao_financeira a,
         	CONCIL_BANC_PEND_TASY d,
         	movto_trans_financ b
	where	b.nr_seq_trans_financ   = a.nr_sequencia
	and	d.NR_SEQ_MOVTO_TRANS    = b.nr_sequencia
	and	d.nr_seq_conciliacao    = nr_seq_conciliacao_w
	and	coalesce(b.nr_seq_concil::text, '') = ''
	and	b.ie_conciliacao        = 'N';
end if;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
