CREATE OR REPLACE FUNCTION obter_valor_pend_guia_data ( nr_interno_conta_p bigint, cd_autorizacao_p text, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_pendente_w		double precision	:= 0;
vl_guia_w		double precision;
vl_pago_w		double precision;
vl_grg_w		double precision;


BEGIN

select	coalesce(sum(a.vl_guia),0)
into STRICT	vl_guia_w
from	conta_paciente_guia a
where 	a.nr_interno_conta	= nr_interno_conta_p
and	coalesce(a.cd_autorizacao,wheb_mensagem_pck.get_Texto(310406))	= coalesce(coalesce(cd_autorizacao_p,a.cd_autorizacao),wheb_mensagem_pck.get_Texto(310406));

select	coalesce(sum(a.vl_pago),0) + coalesce(sum(a.vl_glosado),0)
into STRICT	vl_pago_w
from	convenio_retorno b,
	convenio_retorno_item a
where	a.nr_seq_retorno	= b.nr_sequencia
and	b.ie_status_retorno	= 'F'
and 	a.nr_interno_conta	= nr_interno_conta_p
and 	coalesce(a.cd_autorizacao,wheb_mensagem_pck.get_Texto(310406)) 	= coalesce(coalesce(cd_autorizacao_p,a.cd_autorizacao),wheb_mensagem_pck.get_Texto(310406))
and	coalesce(b.dt_baixa_cr,b.dt_fechamento)	<= dt_referencia_p;

select	coalesce(sum(a.vl_glosa),0) + coalesce(sum(a.vl_pago),0)
into STRICT	vl_grg_w
from	lote_audit_hist c,
	lote_audit_hist_item a,
	lote_audit_hist_guia b
where	b.nr_interno_conta		= nr_interno_conta_p
and	b.nr_sequencia			= a.nr_seq_guia
and	coalesce(b.cd_autorizacao,wheb_mensagem_pck.get_Texto(310406))	= coalesce(coalesce(cd_autorizacao_p,b.cd_autorizacao),wheb_mensagem_pck.get_Texto(310406))
and	b.nr_seq_lote_hist		= c.nr_sequencia
and	c.nr_sequencia			<> (obter_ultima_analise(c.nr_seq_lote_audit))::numeric;

vl_pendente_w	:= coalesce(vl_guia_w,0) - coalesce(vl_pago_w,0) - coalesce(vl_grg_w,0);

return vl_pendente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
