CREATE OR REPLACE FUNCTION obter_valor_perc ( vl_total_p bigint, pr_valor_p bigint) RETURNS bigint AS $body$
DECLARE


vl_perc_w	double precision;


BEGIN
if (vl_total_p IS NOT NULL AND vl_total_p::text <> '') and (pr_valor_p IS NOT NULL AND pr_valor_p::text <> '') then --and
	--(vl_total_p > 0) and
	--(pr_valor_p > 0) then
	begin
	vl_perc_w := dividir((vl_total_p * pr_valor_p), 100);
	end;
end if;
return vl_perc_w;
end;
