CREATE OR REPLACE FUNCTION obter_valor_pf_trib ( cd_tributo_p bigint, cd_pessoa_fisica_p text, dt_referencia_p timestamp, cd_estabelecimento_p bigint, nr_seq_venc_repasse_p bigint default null) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	varchar(255);
dt_mesano_referencia_w	timestamp;
dt_vencimento_repasse_w	timestamp;
vl_tributo_w	pessoa_fisica_trib.vl_tributo%type;


BEGIN
/*Se no cadastro da pessoa física estiver setado o tipo de data para Vencimento considerar o vencimento do repasse (dt_vencimento_repasse_w)
Caso contrário deverá ser verificado a data de mês/ano referência do repasse*/
dt_vencimento_repasse_w	:= dt_referencia_p;

select	max(a.dt_mesano_referencia)
into STRICT	dt_mesano_referencia_w
from	repasse_terceiro a,
	repasse_terceiro_venc b
where	a.nr_repasse_terceiro	= b.nr_repasse_terceiro
and	b.nr_sequencia			= nr_seq_venc_repasse_p;

select	coalesce(sum(a.vl_tributo),0)
into STRICT	vl_tributo_w
from	pessoa_fisica_trib a
where	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p
and	a.cd_tributo		= cd_tributo_p
and	coalesce(coalesce(a.cd_estabelecimento,cd_estabelecimento_p),0)	= coalesce(cd_estabelecimento_p,0)
and	CASE WHEN a.ie_tipo_data='E' THEN dt_mesano_referencia_w  ELSE dt_vencimento_repasse_w END  	between dt_inicio_vigencia and dt_fim_vigencia;

return	vl_tributo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
