CREATE OR REPLACE FUNCTION obter_valor_preco_brasindice (cd_laboratorio_p text, cd_medicamento_p text, cd_apresentacao_p text, ie_tipo_retorno_p text, dt_vigencia_base_p timestamp, dt_vigencia_anterior_p timestamp) RETURNS bigint AS $body$
DECLARE



/* Tipo de Retorno

	V - Valor
	P - Percentual

*/
vl_retorno_w		double precision := 0;
vl_preco_medic_ant_w	double precision := 0;
vl_preco_medic_w	double precision := 0;
pr_anterior_w		double precision := 0;


BEGIN


select	coalesce(sum(vl_preco_medicamento),0)
into STRICT	vl_preco_medic_ant_w
from	brasindice_preco
where	cd_laboratorio		= cd_laboratorio_p
and	cd_medicamento		= cd_medicamento_p
and	cd_apresentacao		= cd_apresentacao_p
and	dt_inicio_vigencia	= dt_vigencia_anterior_p;


select	coalesce(sum(vl_preco_medicamento),0)
into STRICT	vl_preco_medic_w
from	brasindice_preco
where	cd_laboratorio		= cd_laboratorio_p
and	cd_medicamento		= cd_medicamento_p
and	cd_apresentacao		= cd_apresentacao_p
and	dt_inicio_vigencia	= dt_vigencia_base_p;


if (ie_tipo_retorno_p	= 'V') then
	vl_retorno_w	:= vl_preco_medic_ant_w;
elsif (ie_tipo_retorno_p	= 'P') then
	begin

	if (vl_preco_medic_ant_w > 0) then
		pr_anterior_w	:= 100 - (vl_preco_medic_ant_w * 100) / vl_preco_medic_w;
	end if;

	vl_retorno_w	:= pr_anterior_w;

	end;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
