CREATE OR REPLACE FUNCTION obter_valor_prescr_medica_comp (nr_prescricao_p bigint, nm_campo_p text) RETURNS varchar AS $body$
DECLARE


ie_prescricao_anestesica_w	prescr_medica_compl.ie_prescricao_anestesica%type;
ds_retorno_w				varchar(2000);


BEGIN

select 	max(ie_prescricao_anestesica)
into STRICT	ie_prescricao_anestesica_w
from 	prescr_medica_compl
where	nr_prescricao = nr_prescricao_p;

if (upper(nm_campo_p) = 'IE_PRESCRICAO_ANESTESICA') then
	ds_retorno_w := substr(ie_prescricao_anestesica_w,1,2000);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
