CREATE OR REPLACE FUNCTION obter_valor_prescr_solic (nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE



ie_tipo_w 	bigint;
ds_retorno_w	bigint;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	SELECT 	max(ie_tipo)
	INTO STRICT	ie_tipo_w
	FROM 	prescr_solic_bco_sangue
	WHERE 	nr_prescricao = nr_prescricao_p;

	SELECT 	vl_dominio
	into STRICT	ds_retorno_w
	FROM 	valor_dominio
	WHERE 	cd_dominio = 1223
	AND	vl_dominio = ie_tipo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
