CREATE OR REPLACE FUNCTION obter_valor_prev_data_tit_mens ( nr_seq_mensalidade_p bigint, dt_pagamento_p timestamp, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE

 
nr_titulo_w		bigint;
vl_juros_w		double precision;
vl_multa_w		double precision;		
vl_titulo_w		double precision;
vl_retorno_w		double precision := 0;	
			 

BEGIN 
if (nr_seq_mensalidade_p IS NOT NULL AND nr_seq_mensalidade_p::text <> '') then 
	select	pls_obter_dados_mensalidade(nr_seq_mensalidade_p,'T') 
	into STRICT	nr_titulo_w 
	;
 
	select	vl_titulo 
	into STRICT	vl_titulo_w 
	from 	titulo_receber 
	where	nr_titulo = nr_titulo_w;
	 
	vl_juros_w	:= obter_juros_multa_titulo(nr_titulo_w,coalesce(dt_pagamento_p, clock_timestamp()),'R','J');
	vl_multa_w	:= obter_juros_multa_titulo(nr_titulo_w,coalesce(dt_pagamento_p, clock_timestamp()),'R','M');
 
	vl_retorno_w	:= vl_juros_w + vl_multa_w + vl_titulo_w;
end if;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
