SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_processo_aprov ( nr_seq_aprovacao_p bigint, nr_seq_processo_p bigint, cd_pessoa_fisica_p text, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


ie_tipo_w				smallint;
vl_total_w			double precision;
vl_tot_qt_ult_compra_w		double precision;
vl_total_qt_atual_w		double precision;


BEGIN

/*
ie_tipo_w
0 - Ordens
1 - Solicitacao
2 - Requisicao
*/
select	distinct coalesce(max(ie_tipo),0)
into STRICT	ie_tipo_w
from (
	SELECT	-1 ie_tipo
	from	contrato
	where	nr_seq_aprovacao = nr_seq_aprovacao_p
	
union all

	SELECT	0 ie_tipo
	from	ordem_compra_item
	where	nr_seq_aprovacao = nr_seq_aprovacao_p
	
union all
				 		
	select	1 ie_tipo
	from	solic_compra_item
	where	nr_seq_aprovacao = nr_seq_aprovacao_p
	
union all
				 		
	select	2 ie_tipo
	from	item_requisicao_material
	where	nr_seq_aprovacao = nr_seq_aprovacao_p
	
union all
				 		
	select	3 ie_tipo
	from	cot_compra_item
	where	nr_seq_aprovacao = nr_seq_aprovacao_p
	
union all
				 		
	select	4 ie_tipo
	from	nota_fiscal_item
	where	nr_seq_aprovacao = nr_seq_aprovacao_p
	
union all
				 		
	select	5 ie_tipo
	from	projeto_recurso
	where	nr_seq_aprovacao = nr_seq_aprovacao_p) alias2;

if (ie_tipo_w = -1) and (ie_opcao_p = 0) then

	select	c.vl_total_contrato
	into STRICT	vl_total_w
	from	contrato c
	where	c.nr_seq_aprovacao = nr_seq_aprovacao_p;

elsif (ie_tipo_w = -1) and (ie_opcao_p = 1) then
  
   select c.vl_total_contrato
   into STRICT vl_tot_qt_ult_compra_w
   from contrato c
   where c.nr_seq_aprovacao = nr_seq_aprovacao_p;

elsif (ie_tipo_w = -1) and (ie_opcao_p = 2) then
  
   select c.vl_total_contrato
   into STRICT vl_total_qt_atual_w
   from contrato c
   where c.nr_seq_aprovacao = nr_seq_aprovacao_p;

elsif (ie_tipo_w = 0) and (ie_opcao_p = 0) then
	
		select	coalesce(sum(obter_qt_pend_ordem_entrega(b.nr_ordem_compra, b.nr_item_oci) * b.vl_unitario_material),0) vl_total
		into STRICT	vl_total_w		
		from	ordem_compra a,
			ordem_compra_item b
		where	a.nr_ordem_compra = b.nr_ordem_compra
		and	coalesce(b.dt_aprovacao::text, '') = ''
		and	coalesce(b.dt_reprovacao::text, '') = ''
		and	nr_seq_aprovacao = nr_seq_aprovacao_p
		and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';
		
elsif (ie_tipo_w = 0) and (ie_opcao_p = 1) then		
	
	select	coalesce(sum(obter_dados_ult_compra_data(a.cd_estabelecimento, b.cd_material, null, a.dt_liberacao, 1095, 'VUQT')),0) vl_tot_qt_ult_compra		
	into STRICT	vl_tot_qt_ult_compra_w
	from	ordem_compra a,
		ordem_compra_item b
	where	a.nr_ordem_compra = b.nr_ordem_compra
	and	coalesce(b.dt_aprovacao::text, '') = ''
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';
	
elsif (ie_tipo_w = 0) and (ie_opcao_p = 2) then
	
	select	coalesce(sum((obter_dados_ult_compra_data(a.cd_estabelecimento, b.cd_material, null, a.dt_liberacao, 1095, 'VU')) * (obter_quantidade_convertida(b.cd_material, obter_qt_pend_ordem_entrega(b.nr_ordem_compra, b.nr_item_oci), b.cd_unidade_medida_compra,'UMP'))),0) vl_total_qt_atual
	into STRICT	vl_total_qt_atual_w
	from	ordem_compra a,
		ordem_compra_item b
	where	a.nr_ordem_compra = b.nr_ordem_compra
	and	coalesce(b.dt_aprovacao::text, '') = ''
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';
	
elsif (ie_tipo_w = 1) and (ie_opcao_p = 0) then
	
	select	coalesce(sum(b.qt_material * coalesce(b.vl_unit_previsto,0)),0) vl_total
	into STRICT	vl_total_w
	from	solic_compra a,
		solic_compra_item b
	where	a.nr_solic_compra = b.nr_solic_compra
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	coalesce(b.dt_autorizacao::text, '') = ''
	and	nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';
	
elsif (ie_tipo_w = 1) and (ie_opcao_p = 1) then	
	
	select	coalesce(sum(obter_dados_ult_compra_data(a.cd_estabelecimento, b.cd_material, null, a.dt_liberacao, 1095, 'VUQT')),0) vl_tot_qt_ult_compra
	into STRICT	vl_tot_qt_ult_compra_w		
	from	solic_compra a,
		solic_compra_item b
	where	a.nr_solic_compra = b.nr_solic_compra
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	coalesce(b.dt_autorizacao::text, '') = ''
	and	nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';
	
elsif (ie_tipo_w = 1) and (ie_opcao_p = 2) then	
	
	select	coalesce(sum((obter_dados_ult_compra_data(a.cd_estabelecimento, b.cd_material, null, a.dt_liberacao, 1095, 'VU')) * (obter_quantidade_convertida(b.cd_material, b.qt_material, b.cd_unidade_medida_compra,'UMP'))),0) vl_total_qt_atual
	into STRICT	vl_total_qt_atual_w
	from	solic_compra a,
		solic_compra_item b
	where	a.nr_solic_compra = b.nr_solic_compra
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	coalesce(b.dt_autorizacao::text, '') = ''
	and	nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';
	

elsif (ie_tipo_w = 2) and (ie_opcao_p = 0) then	
	
	select	coalesce(sum(b.qt_material_requisitada * coalesce(b.vl_unit_previsto,0)),0)vl_total		
	into STRICT	vl_total_w
	from	requisicao_material a,
		item_requisicao_material b
	where	a.nr_requisicao = b.nr_requisicao
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	coalesce(b.dt_aprovacao::text, '') = ''
	and	nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';

elsif (ie_tipo_w = 2) and (ie_opcao_p = 1) then	
	
	select	coalesce(sum(obter_dados_ult_compra_data(a.cd_estabelecimento, b.cd_material, null, a.dt_liberacao, 1095, 'VUQT')),0) vl_tot_qt_ult_compra
	into STRICT	vl_tot_qt_ult_compra_w
	from	requisicao_material a,
		item_requisicao_material b
	where	a.nr_requisicao = b.nr_requisicao
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	coalesce(b.dt_aprovacao::text, '') = ''
	and	nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';
	
elsif (ie_tipo_w = 2) and (ie_opcao_p = 2) then		
	
	select	coalesce(sum((obter_dados_ult_compra_data(a.cd_estabelecimento, b.cd_material, null, a.dt_liberacao, 1095, 'VU')) * (obter_quantidade_convertida(b.cd_material, b.qt_material_requisitada, b.cd_unidade_medida,'UMP'))),0) vl_total_qt_atual
	into STRICT	vl_total_qt_atual_w
	from	requisicao_material a,
		item_requisicao_material b
	where	a.nr_requisicao = b.nr_requisicao
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	coalesce(b.dt_aprovacao::text, '') = ''
	and	nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';

elsif (ie_tipo_w = 3) and (ie_opcao_p = 0) then

	select	coalesce(sum(a.vl_preco_liquido),0) vl_total
	into STRICT	vl_total_w
	from	cot_compra_resumo a,
		cot_compra_item b,
		cot_compra c
	where	a.nr_cot_compra = b.nr_cot_compra
	and	a.nr_item_cot_compra = b.nr_item_cot_compra
	and	c.nr_cot_compra = b.nr_cot_compra
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	coalesce(b.dt_aprovacao::text, '') = ''
	and	nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';
	
elsif (ie_tipo_w = 3) and (ie_opcao_p = 1) then

	select	coalesce(sum(obter_dados_ult_compra_data(c.cd_estabelecimento, b.cd_material, null, c.dt_cot_compra, 1095, 'VUQT')),0) vl_tot_qt_ult_compra
	into STRICT	vl_tot_qt_ult_compra_w
	from	cot_compra_resumo a,
		cot_compra_item b,
		cot_compra c
	where	a.nr_cot_compra = b.nr_cot_compra
	and	a.nr_item_cot_compra = b.nr_item_cot_compra
	and	c.nr_cot_compra = b.nr_cot_compra
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	coalesce(b.dt_aprovacao::text, '') = ''
	and	nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';

elsif (ie_tipo_w = 3) and (ie_opcao_p = 2) then
	
	select	coalesce(sum((obter_dados_ult_compra_data(c.cd_estabelecimento, b.cd_material, null, c.dt_cot_compra, 1095, 'VU')) * (obter_quantidade_convertida(b.cd_material, a.qt_material, b.cd_unidade_medida_compra,'UMP'))),0) vl_total_qt_atual
	into STRICT	vl_total_qt_atual_w
	from	cot_compra_resumo a,
		cot_compra_item b,
		cot_compra c
	where	a.nr_cot_compra = b.nr_cot_compra
	and	a.nr_item_cot_compra = b.nr_item_cot_compra
	and	c.nr_cot_compra = b.nr_cot_compra
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	coalesce(b.dt_aprovacao::text, '') = ''
	and	nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';

elsif (ie_tipo_w = 4) and (ie_opcao_p = 0) then

	select	coalesce(sum(b.qt_item_nf * coalesce(b.vl_unitario_item_nf,0)),0)vl_total
	into STRICT	vl_total_w
	from	nota_fiscal a,
		nota_fiscal_item b
	where	a.nr_sequencia = b.nr_sequencia
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	coalesce(b.dt_aprovacao::text, '') = ''
	and	b.nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';	

elsif (ie_tipo_w = 4) and (ie_opcao_p = 1) then	
	
	select	coalesce(sum(obter_dados_ult_compra_data(a.cd_estabelecimento, b.cd_material, null, a.dt_liberacao, 1095, 'VUQT')),0) vl_tot_qt_ult_compra
	into STRICT	vl_tot_qt_ult_compra_w
	from	nota_fiscal a,
		nota_fiscal_item b
	where	a.nr_sequencia = b.nr_sequencia
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	coalesce(b.dt_aprovacao::text, '') = ''
	and	b.nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';	
	
elsif (ie_tipo_w = 4) and (ie_opcao_p = 2) then	
	
	select	coalesce(sum((obter_dados_ult_compra_data(a.cd_estabelecimento, b.cd_material, null, a.dt_liberacao, 1095, 'VU')) * (obter_quantidade_convertida(b.cd_material, b.qt_item_nf, b.cd_unidade_medida_compra,'UMP'))),0) vl_total_qt_atual
	into STRICT	vl_total_qt_atual_w
	from	nota_fiscal a,
		nota_fiscal_item b
	where	a.nr_sequencia = b.nr_sequencia
	and	coalesce(b.dt_reprovacao::text, '') = ''
	and	coalesce(b.dt_aprovacao::text, '') = ''
	and	b.nr_seq_aprovacao = nr_seq_aprovacao_p
	and	substr(obter_se_pessoa_deleg_item(b.cd_material,nr_seq_aprovacao_p,nr_seq_processo_p,cd_pessoa_fisica_p,'AC',clock_timestamp()),1,1) = 'S';

elsif (ie_tipo_w = 5) and (ie_opcao_p = 0) then
	select	coalesce(c.vl_projeto,0)
	into STRICT	vl_total_w
	from	projeto_recurso c
	where	c.nr_seq_aprovacao = nr_seq_aprovacao_p;
end if;
		
if (ie_opcao_p = 0) then
	return vl_total_w;
elsif (ie_opcao_p = 1) then
	return vl_tot_qt_ult_compra_w;
elsif (ie_opcao_p = 2) then
	return vl_total_qt_atual_w;	
end if;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_processo_aprov ( nr_seq_aprovacao_p bigint, nr_seq_processo_p bigint, cd_pessoa_fisica_p text, ie_opcao_p bigint) FROM PUBLIC;

